package algebra;

public class Fractions {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		System.out.printf("21: (a) %.4f (b) %.4f",
				(3 / 10f + 4 / 15f),
				(1 / 4f + 1 / 5f)
				);
		
		System.out.println();
		
		
		System.out.printf("22: (a) %.4f (b) %.4f",
				(2 / 3f - 3 / 5f),
				(1 + 5 / 8f - 1 / 6f)
				);
		
		System.out.println();
		
		System.out.printf("23: (a) %.4f (b) %.4f",
				((2 / 3f) * (6 - 3 / 2f)),
				((1 / 4f) * (8 / 9f + 1 / 2f))
				);
		
		System.out.println();
		
		System.out.printf("24: (a) %.4f (b) %.4f",
				((3 + 1 / 4f) * (1 - 4 / 5f)),
				((1 / 2f - 1 / 3f) * (1 / 2f + 1 /3f))
				);
		
		System.out.println();
		
		System.out.printf("25: (a) %.4f (b) %.4f",
				(2 / (2 / 3f) - (2 / 3f) / 2),
				(1 / 12f / (1 / 8f - 1 / 9f))
				);
		
		System.out.println();
		
		System.out.printf("26: (a) %.4f (b) %.4f",
				((2 - 3 / 4f) / (1 / 2f - 1 / 3f)),
				((2 / 5f + 1 / 2f) / (1 / 10f + 3 / 15f))
				);

	}

}
